mapper.update:
UPDATE notice SET 
NOTICE_COUNT = NOTICE_COUNT + 1 
WHERE NOTICE_NUMBER = #{notice_number}

UPDATE notice 
SET 
	NOTICE_TITLE = #{notice_title}, 
	NOTICE_CONTENT = #{notice_content} 
WHERE NOTICE_NUMBER = #{notice_number}

UPDATE sales 
SET SALES_CHECK_BLIND = 1
WHERE SALES_NUMBER = #{sales_number}

UPDATE report 
SET CHECK_COMPLETE = 1
WHERE SALES_NUMBER = #{sales_number}
